import { signInWithEmailAndPassword } from 'firebase/auth';
import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { auth } from '../firebaseConfig';

function Login() {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    function handleSubmit(e) {
        e.preventDefault();
        signInWithEmailAndPassword(auth, email, password)
        .then((userCredential) => {
            const user = userCredential.user;
            props.setSnackBar(true)
            setTimeout(() => {
                props.setSnackBar(false)
            }, 3000)
            return user;
        })
        .then(user => {
            setDoc(doc(db, "users", user.uid), {
                uid: user.uid,
                name,
                email,
                isOnline: true,
            });
            setEmail("")
            setName("")
            setPassword("")
            navigate("login", {replace: true})
        })
        .catch((error) => {
            const errorCode = error.code;
            const errorMessage = error.message;
            setError(errorCode.replace("auth/", "").replace(/-/g, " "))
        });
    }
  return (
    <div className='login'>
        <div className='login-dashborad'>
            <div className='logo-div'>
                <img className='logo' src="/logoName.png" alt="" />
            </div>
            <div className='login-forum'>
                <h1 className='welcome'>Wellcome Back</h1>
                <h2 className='welcome-details'>Wellcome back! Please enter your details.</h2>
                <form action="">

                </form>
                <div className="email-input">
                    <input className='email' id='email' placeholder='  ' type="email" required/>
                    <label htmlFor="email">Email</label>
                </div>
                <div className="email-input">
                    <input className='email' id='password' placeholder='  ' type="password" required/>
                    <label htmlFor="password">password</label>
                </div>
                <div className='login-remember'>
                    <div className='remember-me'>
                        <input id="remember" type="checkbox" />
                        <label htmlFor="remember">Remember me</label>
                    </div>
                    <h3 className='forgot-password'>Forgot password</h3>
                </div>
                <button className='sign-in'>Sign in</button>
                <div className='brand-icons'>
                    <img className='icons' src="/google.png" alt="" />
                    <img className='icons' src="/github.png" alt="" />
                </div>
                <div className='dont-have-account'>
                    <h3 className='go-sign-up'>Don't have an account? <Link style={{textDecoration: "none", color:"#423fde"}} to={"/"}>Sign up</Link></h3>
                </div>
            </div>
        </div>
        <div className='poster'>
            <img className='poster-image' src="/chatPoster.png" alt="" />
        </div>
    </div>
  )
}

export default Login
