import React, { useState, useContext } from 'react'
import { auth, db } from '../firebaseConfig';
import { doc, updateDoc } from 'firebase/firestore';
import { signOut } from 'firebase/auth';
import { useNavigate } from 'react-router';

function Chat() {
    const [dropdown, setDropdown] = useState(false)
    const {user} = useContext(a)

    const navigate = useNavigate();

    const handleDropdown = () => {
        setDropdown(prev => !prev)
    }

    const handleSignOut = () => {
        updateDoc(doc(db, "users", auth.currentUser.uid), {
            isOnline: false,
        })
        .then(
            signOut(auth)
        )
        navigate('/login')
    }

  return (
    <div className='chat'>
        <div className='nav'>
            <nav className='chat-nav'>
                <div className='profile'>
                    <img onClick={handleDropdown} className='profile-image' src="logo.png" alt="" />
                </div>
            </nav>
        {dropdown && <div className='profile-dropdown'>
            <p className='option'>Profile</p>
            <p onClick={handleSignOut} className='option'>Log out</p>
        </div>}
        </div>
    </div>
  )
}

export default Chat
