import { doc, getDoc, updateDoc } from 'firebase/firestore'
import { getDownloadURL, ref, uploadBytes } from 'firebase/storage'
import React, { useEffect, useState } from 'react'
import { auth, db, storage } from '../firebaseConfig'

function Profile(props) {
    const [hovered, setHovered] = useState(false)
    const [img, setImg] = useState("")
    const [user, setUser] = useState()

    useEffect(() => {
        getDoc(doc(db, "users", auth.currentUser.uid)).then((docSnap) => {
            if (docSnap.exists) {
                setUser(docSnap.data())
            }
        })

        if(img) {
            const uploadImg = async () => {
                const imgRef = ref(storage, `avatar/${new Date().getTime()} - ${img.name}`);

                try {
                    const snap = await uploadBytes(imgRef, img);
                const url = await getDownloadURL(ref(storage, snap.ref.fullPath));

                await updateDoc(doc(db, "users", auth.currentUser.uid), {
                    avatar: url,
                    avatarPath: snap.ref.fullPath,
                });

                setImg("");
                } catch {

                }

            };
            uploadImg()
        }
    }, [img])

    function mouseOut() {
        setTimeout(() => {
            setHovered(false)
        },500)
    }

  return (
    <div className='profile-dropdown'>
        <div className='avatar-container'>
            <img onMouseEnter={() => setHovered(true)} onMouseLeave={() => mouseOut} className='avatar-img' src="/21-avatar.svg" alt="" />
            {hovered && <div onMouseEnter={() => setHovered(true)} onMouseLeave={() => mouseOut} className='img-change'>
                <label htmlFor="camera">
                <img className='camera' src="/camera.png" alt="" />
                </label>
                <input onChange={(e) => setImg(e.target.files[0])} id='camera' type="file" accept='image/*' style={{display: "none"}} />
            </div>}
        </div>
        <div className='details'>
            <h1 className='user-email-name'>Name</h1>
            <h3>.............</h3>
            <h1 className='user-email-name'>Email</h1>
            <h3>.............</h3>
            <button onClick={() => props.handleSignOut()} className='log-out'>Logout <img src="/logOut.png" alt="" /></button>
        </div>
    </div>
  )
}

export default Profile
